methods.method:
function Constructor(argobj)
				{
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" } ] });
					return true;
				}

function HandleEvt(oEvtHandler)
				{
					if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false; 
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							$(CLO[argobj.pid].div).find(".cl-slide-name").html(CLS[CLZ.sCurrentSlideId].sName);
							if($(CLO[argobj.pid].div).find(".cl-slide-number").length>0)
							{
								$(CLO[argobj.pid].div).find(".cl-slide-number").html(CLS[CLZ.sCurrentSlideId].GetNumber());
							}
							break;
						}
					}
					return true;
				}

function GetProperty(argobj)
				{
					if(argobj.pid==null || argobj.property==null) return false;
					if(CLO[argobj.pid]==null) return false;
					switch(argobj.property.toLowerCase())
					{
						case "slidename":
						{
							return CLS[CLZ.sCurrentSlideId].sName;
						}
						case "slidenumber":
						{
							return CLS[CLZ.sCurrentSlideId].GetNumber();
						}
					}
					return "";
				}

function Constructor(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(oCurObj.div);
					oCurObj.data =
					{
						aPath: [],
						sDisplayType: oCurObj.jxParams.find("display_type").text(),
						sSlideId: "",
						sChapterId: "",
						sScope: oCurObj.jxParams.children("display_scope").text(),
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.find("allow_link").text()=="strict"),
						bNaviLockedByAction: false,
						bMouseOver: (oCurObj.jxParams.find("allow_mouseover").text()!="never"),
						bLink: (oCurObj.jxParams.find("allow_link").text()!="never"),
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text(),
						jRail: jCurObjDiv.find("[cl-role='progress-rail']"),
						jTemplateV: jCurObjDiv.find("#" + argobj.pid + "_TEMPLATE_V"),
						jTemplateC: jCurObjDiv.find("#" + argobj.pid + "_TEMPLATE_C"),
						jTemplateU: jCurObjDiv.find("#" + argobj.pid + "_TEMPLATE_U")
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(!CL.oPath.bAlternative || oCurObj.data.sScope!="chapter")
					{
						oCurObj.data.aPath = CL.oPath._GetSlideList();
						if(oCurObj.data.sDisplayType=="icons")
						{
							var jElem;
							for(var i=0; i<oCurObj.data.aPath.length; i++)
							{
								jElem = oCurObj.data.jTemplateU.clone(true).appendTo(oCurObj.data.jRail).show().removeClass("cl-progress-elem-template").css({ "display": "inline-block" }).attr({ "cl-id": oCurObj.data.aPath[i] });
								if(oCurObj.data.bMouseOver && !CL.bTouch)
								{
									jElem.on("mouseover", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Over", oMethodArgs: { evt: e, elem: this } }) }).on("mouseout", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Out", oMethodArgs: { evt: e, elem: this } }) });
								}
								if(oCurObj.data.bLink)
								{
									jElem.addClass("cl-linked").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
								}
							}
						}
						else
						{
							$(oCurObj.div).find(".cl-bar").width(0);
						}
					}
					var jCounter = $(oCurObj.div).find(".cl-progress-counter");
					if(jCounter.length!=0)
					{
						jCounter.html(jCounter.html().split("{N}").join('<span class="cl-slide-number" cl-role="slide-number">0</span>').split("{Z}").join('<span class="cl-slide-total" cl-role="slide-total">' + oCurObj.data.aPath.length + '</span>'));
						oCurObj.data.jSlideNumber = jCounter.find("[cl-role='slide-number']");
						oCurObj.data.jSlideTotal = jCounter.find("[cl-role='slide-total']");
					}
					if($(oCurObj.div).find(".cl-progress-slidename-name").length!=0)
					{
						oCurObj.data.jSlideName = $(oCurObj.div).find(".cl-progress-slidename-name");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
                    return true;
                }

function HandleEvt(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					if(oCurObj==null) return false;
					if(oCurObj.div==null) return false;
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
								oCurObj.CallMethod({ sMethod: "Update" });
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="progress" || argobj.sTargets=="all")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
					return true;
                }

function DisableAccess(argobj)
			{
				$(CLO[argobj.pid].div).find(".cl-container").addClass("cl-disabled");
				CLO[argobj.pid].data.sState = "disabled";
				return true;
			}

function EnableAccess(argobj)
			{
				$(CLO[argobj.pid].div).find(".cl-container").removeClass("cl-disabled");
				CLO[argobj.pid].data.sState = "enabled";
				return true;
			}

function UpdateProgress(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(oCurObj.div);
					var iCurrent = -1;
					var iNumber = -1;
					if(!CL.oPath.bAlternative || oCurObj.data.sScope!="chapter")
					{
						iNumber = CL.oPath._GetSlideNumber();
						if(oCurObj.data.sDisplayType=="icons")
						{
							var jCell;
							for(var i=0; i<oCurObj.data.aPath.length; i++)
							{
								jCell = $(oCurObj.div).find("[cl-id='" + oCurObj.data.aPath[i] + "']");
								if(oCurObj.data.aPath[i]==CLZ.sCurrentSlideId)
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-v cl-progress-u").addClass("cl-progress-c").attr({ "src": oCurObj.data.jTemplateC.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-v cl-progress-v-var cl-progress-u cl-progress-u-var").addClass("cl-progress-c cl-progress-c-var");
									}
									if(CLO[argobj.pid].data.bLink)
									{
										jCell.removeClass("cl-linked").unbind("click");
									}
									iCurrent = i;
								}
								else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: oCurObj.data.aPath[i] }))
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-c cl-progress-u").addClass("cl-progress-v").attr({ "src": oCurObj.data.jTemplateV.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-u cl-progress-u-var").addClass("cl-progress-v cl-progress-v-var");
									}
									if(CLO[argobj.pid].data.bLink && !jCell.hasClass("cl-linked"))
									{
										jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
									}
								}
								else
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-c cl-progress-v").addClass("cl-progress-u").attr({ "src": oCurObj.data.jTemplateU.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-v cl-progress-v-var").addClass("cl-progress-u cl-progress-u-var");
									}
									if(CLO[argobj.pid].data.bLink)
									{
										if(((CLZ.bStrictOrder && i<=iCurrent+1) || !CLZ.bStrictOrder) && !jCell.hasClass("cl-linked"))
										{
											jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
										}
									}
								}
							}
						}
						else
						{
							$(oCurObj.div).find(".cl-bar").width(Math.round(iNumber/oCurObj.data.aPath.length*100) + "%");
						}
					}
					else if(CL.oPath.bAlternative && oCurObj.data.sScope=="chapter")
					{
						var sCurrentChapterId = CL.oPath._GetChapterId();
						if(sCurrentChapterId!=oCurObj.data.sChapterId)
						{
							oCurObj.data.aPath = CL.oPath._GetSlideList({ bChapter: true });
							oCurObj.data.sChapterId = sCurrentChapterId;
							if(oCurObj.data.sDisplayType=="icons")
							{
								var jElem;
								oCurObj.data.jRail.children("[cl-id]").remove();
								for(var i=0; i<oCurObj.data.aPath.length; i++)
								{
									jElem = oCurObj.data.jTemplateU.clone(true).appendTo(oCurObj.data.jRail).show().removeClass("cl-progress-elem-template").css({ "display": "inline-block" }).attr({ "cl-id": oCurObj.data.aPath[i] });
									if(oCurObj.data.bMouseOver && !CL.bTouch)
									{
										jElem.on("mouseover", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Over", oMethodArgs: { evt: e, elem: this } }) }).on("mouseout", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Out", oMethodArgs: { evt: e, elem: this } }) });
									}
									if(oCurObj.data.bLink)
									{
										jElem.addClass("cl-linked").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
									}
								}
							}
							else
							{
								jCurObjDiv.find(".cl-bar").width(Math.round(iNumber/oCurObj.data.aPath.length*100) + "%");
							}
							if(oCurObj.data.jSlideTotal!=null)
							{
								oCurObj.data.jSlideTotal.html(CL.oPath._GetLength({ bChapter: true }));
							}
						}
						iNumber = CL.oPath._GetSlideNumber({ bChapter: true });
						if(oCurObj.data.sDisplayType=="icons")
						{
							for(var i=0; i<oCurObj.data.aPath.length; i++)
							{
								jCell = $(oCurObj.div).find("[cl-id='" + oCurObj.data.aPath[i] + "']");
								if(oCurObj.data.aPath[i]==CLZ.sCurrentSlideId)
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-v cl-progress-u").addClass("cl-progress-c").attr({ "src": oCurObj.data.jTemplateC.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-v cl-progress-v-var cl-progress-u cl-progress-u-var").addClass("cl-progress-c cl-progress-c-var");
									}
									if(CLO[argobj.pid].data.bLink)
									{
										jCell.removeClass("cl-linked").unbind("click");
									}
									iCurrent = i;
								}
								else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: oCurObj.data.aPath[i] }))
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-c cl-progress-u").addClass("cl-progress-v").attr({ "src": oCurObj.data.jTemplateV.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-u cl-progress-u-var").addClass("cl-progress-v cl-progress-v-var");
									}
									if(CLO[argobj.pid].data.bLink && !jCell.hasClass("cl-linked"))
									{
										jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
									}
								}
								else
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-c cl-progress-v").addClass("cl-progress-u").attr({ "src": oCurObj.data.jTemplateU.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-v cl-progress-v-var").addClass("cl-progress-u cl-progress-u-var");
									}
									if(CLO[argobj.pid].data.bLink)
									{
										if(((CLZ.bStrictOrder && i<=iCurrent+1) || !CLZ.bStrictOrder) && !jCell.hasClass("cl-linked"))
										{
											jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
										}
									}
								}
							}
						}
						else
						{
							jCurObjDiv.find(".cl-bar").width(Math.round(iNumber/oCurObj.data.aPath.length*100) + "%");
						}
					}
					if(oCurObj.data.jSlideNumber!=null)
					{
						oCurObj.data.jSlideNumber.html(iNumber);
					}
					if(oCurObj.data.jSlideName!=null)
					{
						oCurObj.data.jSlideName.html(CLS[CLZ.sCurrentSlideId].sName);
					}
					return true;
                }

function Navigate(argobj)
                {
					if($(CLO[argobj.pid].div).find("div.cl-container").hasClass("cl-disabled")) return false;
					var sTargetId = $(argobj.elem).attr("cl-id");
					if(argobj.override==true || CL.Navigation.Allowed({ sTarget: "slide", sTargetId: sTargetId }))
					{
						CLO[argobj.pid].CallMethod({ sMethod: "DisableAccess" });
						CL.Navigation.GoTo({ sTargetType: "slide", sTargetId: sTargetId });
					}
					CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
					if(oCurObj.data.sSoundClick!="none")
					{
						CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
					}
                    return true;
                }

function Over(argobj)
				{
					var sTargetId = $(argobj.elem).attr("cl-id");
					var oCurObj = CLO[argobj.pid];
					$(CLO[argobj.pid].div).find("div.cl-over").html($(CL.jxModule).find("slide[id='" + sTargetId + "']").attr("name"));
					CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
					if(oCurObj.data.sSoundOver!="none")
					{
						CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
					}
					return true;
				}

function Out(argobj)
				{
					$(CLO[argobj.pid].div).find("div.cl-over").html("&nbsp;");
					return true;
				}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				var sRole = oCurObj.jxParams.children("role").text();
				var sTarget = (sRole.indexOf("slide")==-1) ? "frame" : "slide";
				var sDir = "";
				var sId;
				if(sRole=="closemodule")
				{
					oCurObj.data =
					{
						sRole: "close",
						sState: "enabled",
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
				}
				else
				{
					var bSwipe = false;
					if(sRole.indexOf("next")!=-1)
					{
						sDir = "next";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("prev")!=-1)
					{
						sDir = "prev";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("first")!=-1)
					{
						sDir = "first";
					}
					else if(sRole.indexOf("last")!=-1)
					{
						sDir = "last";
					}
					else if(sRole=="thisslide")
					{
						sDir = "this";
					}
					else
					{
						sId = oCurObj.jxParams.children((sRole=="selectedslide") ? "selectedslide" : "selectedframe").text();
					}
					oCurObj.data =
					{
						sRole: sRole,
						sTarget: sTarget,
						sDir: sDir,
						sId: sId,
						sFrameId: "",
						sSlideId: "",
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
						bNaviLockedByAction: false,
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(oCurObj.data.sRole=="close")
					{
						oCurObj.data.bDisplayConfirm = (oCurObj.jxParams.children("display_closemodule_msg").text()=="yes");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_FRAME_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_FRAME_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				}
				if(CL.bTouch)
				{
					if(bSwipe)
					{
						if(sDir=="next")
						{
							$(CL.oBoard).on("swipeleft", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}
						else if(sDir=="prev")
						{
							$(CL.oBoard).on("swiperight", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}

					}
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				else
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sTarget=="slide")
				{
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets==oCurObj.data.sDir || (argobj.sTargets=="restart" && oCurObj.data.sDir=="this") || argobj.sTargets=="all" || (argobj.sTargets=="buttons" && (oCurObj.data.sDir=="next" || oCurObj.data.sDir=="prev")) || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}					
					}
				}
				else
				{
					switch(argobj.evt)
					{
						case "EVENT_FRAME_OPENED":
						{
							if(oCurObj.data.sFrameId!=CLZ.sCurrentFrameId)
							{
								oCurObj.data.sFrameId = CLZ.sCurrentFrameId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_FRAME_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="all" || argobj.sTargets=="buttons" || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}					
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				if(argobj.evt==null) return false;
				if(oCurObj.data.sState=="disabled" || $(".cl-modal-mask").is(":visible")) return false;
				switch(argobj.evt.type)
				{
					case "swiperight":
					case "swipeleft":
					{
						oCurObj.CallMethod({ sMethod: "Navigate" });
						break;
					}
				}
				return true;
			}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jBtn = $(CLO[argobj.pid].div).find(".cl-btn");
				if(jBtn.hasClass("cl-disabled") && argobj.sState!="enabled") return false;
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "disabled":
					{
						jBtn.removeClass("cl-idle cl-over").addClass("cl-disabled");
						CLO[argobj.pid].data.sState = "disabled";
						break;
					}
					case "enabled":
					{
						jBtn.removeClass("cl-disabled").addClass("cl-idle");
						CLO[argobj.pid].data.sState = "enabled";
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
				return true;
			}

function EnableAccess(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sRole=="close")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "enabled" } });
				}
				else
				{
					var oParams = { sTarget: CLO[argobj.pid].data.sTarget };
					if(CLO[argobj.pid].data.sDir=="")
					{
						oParams.sTargetId = CLO[argobj.pid].data.sId;
					}
					else
					{
						oParams.sDir = CLO[argobj.pid].data.sDir;
					}
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: ((argobj.override==true || CL.Navigation.Allowed(oParams)) ? "enabled" : "disabled") } });
				}
				return true;
			}

function Navigate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var oParams = { sTargetType: oCurObj.data.sTarget };
				if(oCurObj.data.sDir=="")
				{
					oParams.sTargetId = oCurObj.data.sId;
				}
				else
				{
					oParams.sTargetDir = oCurObj.data.sDir;
				}
				if(argobj.override==true || CL.Navigation.Allowed(oParams))
				{
					oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
					CL.Navigation.GoTo(oParams);
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function GetProperty(argobj)
			{
				return CLO[argobj.pid].GetProperty(argobj);
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="disabled") return false;
				if(oCurObj.data.sRole=="close")
				{
					if(oCurObj.data.bDisplayConfirm)
					{
						if(confirm(CLO[argobj.pid].jxParams.children("closemodule_msg").text()))
						{
							window.close();
						}
					}
					else
					{
						window.close();
					}
				}
				else
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Navigate" });
				}
				return true;
			}

function Constructor(argobj)
				{
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(CLO[argobj.pid].div);
					oCurObj.data =
					{
						sRole: oCurObj.jxParams.children("role").text(),
						sState: "on",
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					jCurObjDiv.find(".cl-btn").children("div:first").addClass("cl-idle");
					if(CL.bTouch)
					{
						jCurObjDiv.find(".cl-btn").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
					}
					else
					{
						jCurObjDiv.find(".cl-btn").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
					}
					oCurObj.data.sTitleOn = oCurObj.jxParams.children("alt_on_" + oCurObj.data.sRole).text();
					oCurObj.data.sTitleOff = oCurObj.jxParams.children("alt_off_" + oCurObj.data.sRole).text();
					switch(oCurObj.data.sRole)
					{
						case "sound":
						{
							if(!CLZ.bSoundOn)
							{
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "off" } });
								CL.Sound.Disable();
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
								CL.Sound.Enable();
							}
							break;
						}
						case "fitscreen":
						{
							if(CLZ.nZoom==1)
							{
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "off" } });
							}
							break;
						}
						case "method":
						{
							oCurObj.data.sTargetId = oCurObj.jxParams.children("target_object").text();
							if(CLO[oCurObj.data.sTargetId]==null)
							{
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
							}
							else
							{
								if(CLO[oCurObj.data.sTargetId].oMethods["Open"]!=null && CLO[oCurObj.data.sTargetId].oMethods["Close"]!=null)
								{
									if(CLO[oCurObj.data.sTargetId].aButtonIds==null)
									{
										CLO[oCurObj.data.sTargetId].aButtonIds = [];
									}
									CLO[oCurObj.data.sTargetId].aButtonIds.push(argobj.pid);
									if($(CLO[oCurObj.data.sTargetId].div).is(":visible"))
									{
										oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "off" } });
									}
									else
									{
										oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
									}
								}
							}
							break;
						}
					}
					return true;
				}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(CLO[argobj.pid].div);
				var jBtn = $(CLO[argobj.pid].div).find(".cl-btn");
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.children("div:first").removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.children("div:first").removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "on":
					{
						jBtn.removeClass("cl-btn-off").addClass("cl-btn-on");
						CLO[argobj.pid].data.sState = "on";
						break;
					}
					case "off":
					{
						jBtn.removeClass("cl-btn-on").addClass("cl-btn-off");
						CLO[argobj.pid].data.sState = "off";
						break;
					}
				}
				return true;
			}

function On(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="on") return false;
				$(oCurObj.div).find(".cl-btn").removeClass("cl-btn-off").addClass("cl-btn-on");
				oCurObj.data.sState = "on";
				return true;
			}

function Off(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="off") return false;
				var jBtn = $(oCurObj.div).find(".cl-btn").removeClass("cl-btn-on").addClass("cl-btn-off");
				oCurObj.data.sState = "off";
				return true;
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jBtn = $(oCurObj.div).find(".cl-btn");
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				switch(oCurObj.data.sRole)
				{
					case "sound":
					{
						if(oCurObj.data.sState=="on")
						{
							CL.Sound.Enable();
							oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "off" } });
							jBtn.attr({ "title": oCurObj.data.sTitleOff });
						}
						else
						{
							CL.Sound.Disable();
							oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
							jBtn.attr({ "title": oCurObj.data.sTitleOn });
						}
						break;
					}
					case "fitscreen":
					{
						if(oCurObj.data.sState=="on")
						{
							CL.Zoom({ bFit: true, bResize: true, bNaviBtn: true });
							oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "off" } });
							jBtn.attr({ "title": oCurObj.data.sTitleOff });
						}
						else
						{
							CL.Zoom({ bFit: false });
							oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
							jBtn.attr({ "title": oCurObj.data.sTitleOn });
						}
						break;
					}
					case "contents":
					case "method":
					{
						if(CLO[oCurObj.data.sTargetId]!=null)
						{
							if($(CLO[oCurObj.data.sTargetId].div).is(":visible"))
							{
								CLO[oCurObj.data.sTargetId].CallMethod({ sMethod: "Close" });
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "on" } });
								jBtn.attr({ "title": oCurObj.data.sTitleOff });
							}
							else
							{
								CLO[oCurObj.data.sTargetId].CallMethod({ sMethod: "Open" });
								oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "off" } });
								jBtn.attr({ "title": oCurObj.data.sTitleOn });
							}
						}
						break;
					}
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				oCurObj.data =
				{
					iSlideNum: 0,
					jChapterNumber: [],
					jChapterTotal: [],
					jNumber: [],
					jTotal: [],
					sDisplayType: oCurObj.jxParams.children("display_type").text(),
					sSlideId: ""
				};
				switch(oCurObj.data.sDisplayType)
				{
					case "plain": 
					{
						oCurObj.data.jNumber = jCurObjDiv.find("[cl-role='slide-number']");
						oCurObj.data.jTotal = jCurObjDiv.find("[cl-role='slide-total']");
						oCurObj.data.jTotal.html(CL.oPath._GetLength());
						break;
					}
					case "sub_module": 
					{
						var jText = jCurObjDiv.find("[cl-role='position-text']");
						jText.html( jText.html().split("{{N}}").join('<span class="cl-value" cl-role="slide-number"></span>').split("{{M}}").join('<span class="cl-total" cl-role="slide-total"></span>') );
						oCurObj.data.jNumber = jText.find("[cl-role='slide-number']");
						oCurObj.data.jTotal = jText.find("[cl-role='slide-total']");
						break;
					}
					case "sub_chapter": 
					{
						var jText = jCurObjDiv.find("[cl-role='position-text']");
						jText.html( jText.html().split("{{X}}").join('<span class="cl-value" cl-role="slide-chapter-number"></span>').split("{{Y}}").join('<span class="cl-total" cl-role="slide-chapter-total"></span>') );
						oCurObj.data.jChapterNumber = jText.find("[cl-role='slide-chapter-number']");
						oCurObj.data.jChapterTotal = jText.find("[cl-role='slide-chapter-total']");
						break;
					}
					case "sub_all": 
					{
						var jText = jCurObjDiv.find("[cl-role='position-text']");
						jText.html( jText.html().split("{{N}}").join('<span class="cl-value" cl-role="slide-number"></span>').split("{{M}}").join('<span class="cl-total" cl-role="slide-total"></span>').split("{{X}}").join('<span class="cl-value" cl-role="slide-chapter-number"></span>').split("{{Y}}").join('<span class="cl-total" cl-role="slide-chapter-total"></span>') );
						oCurObj.data.jChapterNumber = jText.find("[cl-role='slide-chapter-number']");
						oCurObj.data.jChapterTotal = jText.find("[cl-role='slide-chapter-total']");
						oCurObj.data.jNumber = jText.find("[cl-role='slide-number']");
						oCurObj.data.jTotal = jText.find("[cl-role='slide-total']");
						break;
					}
				}
				if(oCurObj.data.jTotal.length!=0)
				{
					oCurObj.data.jTotal.html(CL.oPath._GetLength());
				}
				CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" } ] });
				return true;
			}

function HandleEvt(oEvtHandler)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				if(oCurObj.div==null) return false; 
				switch(argobj.evt)
				{
					case "EVENT_SLIDE_OPENED":
					{
						if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
						{
							CLO[argobj.pid].CallMethod({ sMethod: "Update" });
						}
						break;
					}
				}
				return true;
			}

function Update(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				if(oCurObj.data.jNumber.length!=0)
				{
					oCurObj.data.jNumber.html(CL.oPath._GetSlideNumber());
				}
				if(oCurObj.data.jChapterNumber.length!=0)
				{
					oCurObj.data.jChapterNumber.html(CL.oPath._GetSlideNumber({ bChapter: true }));
				}
				if(oCurObj.data.jChapterTotal.length!=0)
				{
					oCurObj.data.jChapterTotal.html(CL.oPath._GetLength({ bChapter: true }));
				}
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				if(CLO[argobj.pid]==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "currentposition": 
					{
						return CLO[argobj.pid].data.iSlideNum;
					}
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurDiv = $(oCurObj.div);
				oCurObj.bShowInternal = true;
				var jContainer = jCurDiv.find(".cl-container");
				jContainer.find(".cl-btn").on("click", function () { oCurObj.CallMethod({ sMethod: "Close" }) });
				oCurObj.initial.nZ = 5000;
				oCurObj.data =
				{
					aTogglers: [],
					bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
					bNaviLockedByAction: false,
					bModal: (oCurObj.jxParams.children("modal").text()=="yes"),
					bSearchTip: (oCurObj.jxParams.children("search_tip").text()=="yes"),
					sSlideId: "",
					sRole: oCurObj.jxParams.children("contenttype").text(),
					sState: "enabled",
					sPosition: "closed",
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				if(oCurObj.data.sSoundClick=="custom")
				{
					oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
				}
				if(oCurObj.data.sSoundOver=="custom")
				{
					oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
				}
				var jxToggle = CL.jxModule.find("param > target_object");
				var sTogglerId;
				for(var i=0; i<jxToggle.length; i++)
				{
					if($(jxToggle[i]).text()==argobj.pid)
					{
						sTogglerId = $(jxToggle[i]).parent().attr("objectid");
						oCurObj.data.aTogglers.push(sTogglerId);
						if(oCurObj.data.sRole=="contents")
						{
							if(CLO[sTogglerId]!=null)
							{
								if(CLO[sTogglerId].data!=null)
								{
									CLO[sTogglerId].data.sRole = "contents";
								}
							}
						}
					}
				}
				var iTranIn = parseInt(oCurObj.jxParams.children("tranin").text(),10);
				if(iTranIn==-2 && oCurObj.tranin!=null)
				{
					oCurObj.data.bTranIn = true;
					oCurObj.data.iTranIn = oCurObj.tranin.transition;
					oCurObj.data.iDurIn = oCurObj.tranin.duration;
				}
				else if(iTranIn>=0)
				{
					oCurObj.data.bTranIn = true;
					oCurObj.data.iTranIn = iTranIn;
					oCurObj.data.iDurIn = parseInt(oCurObj.jxParams.children("durin").text(),10);
				}
				else
				{
					oCurObj.data.bTranIn = false;
				}
				var iTranOut = parseInt(oCurObj.jxParams.children("tranout").text(),10);
				if(iTranOut==-2 && oCurObj.tranout!=null)
				{
					oCurObj.data.bTranOut = true;
					oCurObj.data.iTranOut = oCurObj.tranout.transition;
					oCurObj.data.iDurOut = oCurObj.tranout.duration;
				}
				else if(iTranOut>=0)
				{
					oCurObj.data.bTranOut = true;
					oCurObj.data.iTranOut = iTranOut;
					oCurObj.data.iDurOut = parseInt(oCurObj.jxParams.children("durout").text(),10);
				}
				else
				{
					oCurObj.data.bTranOut = false;
				}
				oCurObj.CallMethod({ sMethod: "InitRole" });
				jCurDiv.hide();
				CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				return true;
			}

function HandleEvt(oEvtHandler, oArg)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false; 
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						oCurObj.data.sPosition = "open";
						break;
					}
					case "EVENT_SLIDE_OPENED":
					{
						oCurObj.CallMethod({ sMethod: "Close" });
						if(oCurObj.data.sRole=="contents" || oCurObj.data.sRole=="settings" || oCurObj.data.sRole=="comment")
						{
							oCurObj.CallMethod({ sMethod: "Update" });
						}
						if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
						{
							oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
							oCurObj.data.bNaviLockedByAction = false;
						}
						oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "Disable" : "Enable") });
						break;
					}
					case "EVENT_SLIDE_COMPLETE":
					{
						oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "Disable" : "Enable") });
						break;
					}
					case "EVENT_NAVIGATION":
					{
						if(argobj.sTargets!=null)
						{
							if(argobj.sTargets=="tabs" || argobj.sTargets=="all" || (argobj.sTargets=="contents" && oCurObj.data.sRole=="contents"))
							{
								switch(argobj.sAction)
								{
									case "off":
									{
										oCurObj.data.bNaviLockedByAction = true;
										oCurObj.CallMethod({ sMethod: "Disable" });
										break;
									}
									case "on":
									default:
									{
										oCurObj.data.bNaviLockedByAction = false;
										oCurObj.CallMethod({ sMethod: "Enable" });
										break;
									}
								}
							}
						}
						break;
					}					
				}
				return true;
			}

function InitRole(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jContainer = $(oCurObj.div).find(".cl-container");
				var iPadding = +oCurObj.jxParams.find("panel_padding").text();
				var iBorder = +oCurObj.jxParams.find("panel_borderwidth").text();
				var iFullH = oCurObj.initial.h;
				var iRadius = +oCurObj.jxParams.find("panel_radius").text();
				var iCloseH = (iRadius > iPadding) ? iRadius + 20 : iPadding + 20;
				var iHH = 0;
				var jHeader = jContainer.find(".cl-body-header");
				if(jHeader.length>0)
				{
					iHH = jHeader.outerHeight();
					if(iHH<iCloseH) iHH = iCloseH;
				}
				else
				{
					iHH = iCloseH;
				}
				iH = iFullH - iHH - 2*iPadding - 2*iBorder;
				jContainer.find(".cl-body-contents").height(iH);
				switch(oCurObj.data.sRole)
				{
					case "contents":
					{
						var jTemplate = jContainer.find("li.slide-item-template");
						var jUL = jContainer.find("ul.slide-list");
						var jxSlide;
						var jLI;
						var sSlideId;
						var bPrevVisited = true;
						var iIndent;
						var iPrevIndent = 0;
						var aPath = CL.oPath._GetSlideList();
						for(var i=0; i<aPath.length; i++)
						{
							sSlideId = aPath[i];
							jxSlide = CL.jxModule.find("slide[id='" + sSlideId + "']");
							jLI = jTemplate.clone(true).removeClass("slide-item-template").addClass("slide-item").attr({ "data-slide-id": sSlideId }).appendTo(jUL);
							jLI.on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { elem: this } }) });
							jLI.find(".item-inner").html(jxSlide.attr("name"));
							if(jxSlide.attr("indent")!=null)
							{
								iIndent = +jxSlide.attr("indent");
								if(iIndent!=0)
								{
									jLI.addClass("indent-" + iIndent);
									if(iIndent>iPrevIndent && CL.axSlides[i-1]!=0)
									{
										jLI.prev().addClass("open");
										iPrevIndent = iIndent;
									}
								}
								else
								{
									iPrevIndent = 0;
								}
							}
							else
							{
								iPrevIndent = 0;
							}
							if(sSlideId==CLZ.sCurrentSlideId)
							{
								jLI.addClass("current");
								bPrevVisited = true;
							}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: sSlideId }))
							{
								jLI.addClass("allowed visited");
								bPrevVisited = true;
							}
							else
							{
								if(bPrevVisited==true)
								{
									jLI.addClass("allowed unvisited");
									bPrevVisited = false;
								}
								else if(!CLZ.bStrictOrder)
								{
									jLI.addClass("allowed unvisited");
								}
								else
								{
									jLI.addClass("forbidden unvisited");
								}
							}
						}
						break;
					}
					case "search":
					{
						jContainer.find(".cl-search-results-list, .cl-search-found, .cl-search-no-results").hide();
						jContainer.find(".cl-search-btn").on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Search" }) });
						jContainer.find(".cl-search-field").on("keydown", function () { CLO[argobj.pid].CallMethod({ sMethod: "SearchTip" }) });
						break;
					}
					case "glossary":
					{
						var jLetterBox = jContainer.find(".cl-letters-box");
						if(jLetterBox.length!=0)
						{
							var iMaxW = -1;
							var jLetters = jLetterBox.find(".cl-letter-item");
							jLetters.each(function ()
							{
								var iW = $(this).width();
								if(iW>iMaxW) iMaxW = iW;
							});
							jLetters.each(function ()
							{
								$(this).width(iMaxW);
							});
							iHH = jLetterBox.outerHeight();
							iH = iH - iHH;
						}
						jContainer.find(".cl-terms-list-container").height(iH);
						jContainer.find(".cl-terms-desc").height(iH);
						iH -= 2*iPadding;
						jContainer.find(".cl-terms-desc-container").height(iH);
						jContainer.find(".cl-terms-desc-default-table").height(iH);
						jContainer.find(".cl-terms-desc-default-cell").height(iH);
						if(jContainer.find(".cl-letter-item").length>0)
						{
							jContainer.find(".cl-letter-item").on("click", function () { oCurObj.CallMethod({ sMethod: "Filter", oMethodArgs: { elem: this } }) });
						}
						jContainer.find(".cl-term-item").on("click", function () { oCurObj.CallMethod({ sMethod: "Display", oMethodArgs: { elem: this } }) });
						break;
					}
					case "calculator":
					{
						jContainer.find(".calc-btn").on("click", function () { oCurObj.CallMethod({ sMethod: "HandleCalc", oMethodArgs: { elem: this } }) });
						oCurObj.data.sOperandA = "na";
						oCurObj.data.sOperatorA = "na";
						oCurObj.data.sOperandB = "na";
						oCurObj.data.sLastBtn = "na";
						oCurObj.data.sMemory = "na";
						oCurObj.data.bClean = false;
						oCurObj.data.bEqual = false;
						oCurObj.data.bPercent = false;
						break;
					}
					case "settings":
					{
						oCurObj.data.sObjToDisplay = oCurObj.jxParams.children("settings_display").text();
						oCurObj.data.bCustomIcons = (oCurObj.jxParams.children("settings_icons").text()=="custom");
						oCurObj.data.sScoreDisplayType = oCurObj.jxParams.children("score_style").text();
						var jTemplate = jContainer.find(".cl-sett-row-template");
						var jTable = jTemplate.parents("table:first");
						var jTR;
						var jTD;
						for(var sObjId in CLJ)
						{
							if(oCurObj.data.sObjToDisplay=="main" && CLJ[sObjId].bModule.toString()!="1") continue;
							if(oCurObj.data.sObjToDisplay=="other" && CLJ[sObjId].bModule.toString()=="1") continue;
							jTR = jTemplate.clone(true).attr({ "data-obj-id": sObjId }).removeClass("cl-sett-row-template");
							jTR.children("td.cl-sett-name").html(CLJ[sObjId].sName);
							if(oCurObj.data.sScoreDisplayType=="raw")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
							}
							else if(oCurObj.data.sScoreDisplayType=="rawmax")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore + "(" + CLJ[sObjId].nMax + ")");
							}
							else if(oCurObj.data.sScoreDisplayType=="scaled")
							{
								if(CLJ[sObjId].nMax!=0)
								{
									jTR.children("td.cl-sett-score").html( Math.round(100*CLJ[sObjId].nRawScore/CLJ[sObjId].nMax)/100 + "%");
								}
								else
								{
									jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
								}
							}
							if(oCurObj.data.bCustomIcons)
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS),"data-obj-id": sObjId });
								jTD.find("img").hide();
								jTD.find("img.sett-cs-" + CLJ[sObjId].sCS).show();
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS),"data-obj-id": sObjId });
								jTD.find("img").hide();
								jTD.find("img.sett-ss-" + CLJ[sObjId].sSS).show();
							}
							else
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS),"data-obj-id": sObjId });
								jTD.find("div.cl-sett-status").addClass("cl-sett-status-" + CLJ[sObjId].sCS);
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS),"data-obj-id": sObjId });
								jTD.find("div.cl-sett-status").addClass("cl-sett-status-" + CLJ[sObjId].sSS);
							}
							if(CLJ[sObjId].bModule.toString()=="1")
							{
								jTR.insertAfter(jTable.find("tr.sett-main-obj-header"));
							}
							else
							{
								jTR.appendTo(jTable);
							}
						}
						break;
					}
					case "comment":
					{
						oCurObj.data.sIfNoComment = oCurObj.jxParams.children("comment_display").text();
						break;
					}
				}
			}

function HandleCalc(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="calculator") return false;
				if(argobj.elem==null) return false;
				var sKey = $(argobj.elem).attr("data-btn");
				if(sKey==null || sKey=="") return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jDisplay = jContainer.find(".calc-display");
				var jMemInd = jContainer.find(".calc-memind");
				var sCurString = $.trim(jDisplay.html() + "");
				if($(argobj.elem).hasClass("calc-num")) // numeric btn or .
				{
					sKey = sKey + "";
					if(oCurObj.data.bEqual==true)
					{
						oCurObj.data.sOperandA = "na";
						oCurObj.data.sOperatorA = "na";
						oCurObj.data.sOperandB = "na";
						oCurObj.data.bEqual = false;
					}
					if(oCurObj.data.bClean==true)
					{
						sCurString = "0";
						jDisplay.html("0");
						oCurObj.data.sOperandB = "na";
						oCurObj.data.bClean = false;
					}
					if(sCurString=="0" && sKey=="0") return false;
					if(sKey=="." && sCurString.indexOf(".")!=-1) return false;
					if(sCurString.length>=17)
					{
						if(sCurString.indexOf("!")==-1) jDisplay.html("!"+sCurString);
						return false;
					}
					if(sCurString.length==1 && sCurString=="0" && sKey!=".")
					{
						jDisplay.html(sKey);
						return true;
					}
					jDisplay.html(sCurString + sKey);
				}
				else
				{
					if(sCurString.indexOf("ERROR")!=-1 && sKey!="C") return false;
					switch(sKey)
					{
						case "M+":
						{
							if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
							jMemInd.html("M");
							if(oCurObj.data.sMemory=="na")
							{
								oCurObj.data.sMemory = sCurString;
							}
							else
							{
								var nM1 = parseFloat(oCurObj.data.sMemory);
								var nM2 = parseFloat(sCurString);
								nM1 = nM1 + nM2;
								oCurObj.data.sMemory = nM1.toString();
							}
							break;
						}
						case "MS":
						{
							if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
							jMemInd.html("M");
							if(oCurObj.data.sMemory=="na")
							{
								oCurObj.data.sMemory = "-" + sCurString;
							}
							else
							{
								var nM1 = parseFloat(oCurObj.data.sMemory);
								var nM2 = parseFloat(sCurString);
								nM1 = nM1 - nM2;
								oCurObj.data.sMemory = nM1.toString();
							}
							break;
						}
						case "MR":
						{
							if(oCurObj.data.sMemory!="na")
							{
								jDisplay.html(oCurObj.data.sMemory);
							}
							break;
						}
						case "MC":
						{
							oCurObj.data.sMemory = "na";
							jMemInd.html("&nbsp;");
							break;
						}
						case "C":
						{
							oCurObj.data.sOperandA = "na";
							oCurObj.data.sOperatorA = "na";
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = false;
							jDisplay.html("0");
							break;
						}
						case "CE":
						{
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = false;
							jDisplay.html("0");
							break;
						}
						case "Backspace":
						{
							if(sCurString.length>1)
							{
								sCurString = sCurString.substr(0,sCurString.length-1);
							}
							jDisplay.html(sCurString);
							break;
						}
						case "equal":
						{
							if(oCurObj.data.bPercent==true)
							{
								return false;
							}
							oCurObj.data.bEqual = true;
							if(oCurObj.data.sOperandA!="na")
							{
								oCurObj.CallMethod({ sMethod: "Calculate", oMethodArgs: { sKey: sKey } });
							}
							break;
						}
						case "power":
						case "plus":
						case "minus":
						case "mult":
						case "divide":
						{
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							if(oCurObj.data.bPercent==true)
							{
								oCurObj.data.bPercent = false;
							}
							if(sCurString.indexOf("!")!=-1)
							{
								sCurString = sCurString.substr(1);
								jDisplay.html(sCurString);
							}
							if(oCurObj.data.sOperandA=="na" || oCurObj.data.bClean==true)
							{
								oCurObj.data.sOperandA = sCurString;
								oCurObj.data.sOperatorA = sKey;
								oCurObj.data.bClean = true;
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "Calculate", oMethodArgs: { sKey: sKey } });
							}
							break;
						}
						case "negate":
						{
							if(sCurString=="0") return false;
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							if(oCurObj.data.bPercent==true)
							{
								oCurObj.data.bPercent = false;
							}
							if(sCurString.indexOf("-")!=-1)
							{
								sCurString = sCurString.substr(1);
							}
							else
							{
								sCurString = "-"+sCurString;
							}
							jDisplay.html(sCurString);
							break;
						}
						case "recip":
						{
							if(sCurString=="0")
							{
								oCurObj.data.sOperandA = "na";
								oCurObj.data.sOperatorA = "na";
								oCurObj.data.sOperandB = "na";
								oCurObj.data.sLastBtn = "na";
								oCurObj.data.bClean = false;
								jDisplay.html("ERROR: DIV 0");
								return false;
							}
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							if(oCurObj.data.bPercent==true)
							{
								oCurObj.data.bPercent = false;
							}
							if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
							var nValue = 1/parseFloat(sCurString);
							sCurString = nValue.toString();
							if(sCurString.length>=20)
							{
								sCurString = sCurString.substring(0,19);
							}
							jDisplay.html(sCurString);
							break;
						}
						case "percent":
						{
							if(oCurObj.data.sOperandA=="na") return false;
							if(oCurObj.data.bPercent==true) return false;
							oCurObj.data.bPercent = true;
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							oCurObj.CallMethod({ sMethod: "Calculate", oMethodArgs: { sKey: sKey } });
							oCurObj.data.sOperandB = "na";
							break;
						}
					}

				}
				return true;
			}

function Calculate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="calculator") return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jDisplay = jContainer.find(".calc-display");
				var jMemInd = jContainer.find(".calc-memind");
				var sCurString = $.trim(jDisplay.html() + "");
				if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
				var nValue1 = parseFloat(oCurObj.data.sOperandA);
				if(oCurObj.data.sOperandB=="na")
				{
					oCurObj.data.sOperandB = sCurString
				}
				var nValue2 = parseFloat(oCurObj.data.sOperandB);
				var nResult = 0;
				switch(oCurObj.data.sOperatorA)
				{
					case "plus":
					{
						nResult = (nValue1*1000 + nValue2*1000)/1000;
						break;
					}
					case "minus":
					{
						nResult = (nValue1*1000 - nValue2*1000)/1000;
						break;
					}
					case "mult":
					{
						nResult = nValue1 * nValue2;
						if(oCurObj.data.bPercent==true)
						{
							nResult = nResult/100;
						}
						break;
					}
					case "divide":
					{
						if(nValue2==0)
						{
							oCurObj.data.sOperandA = "na";
							oCurObj.data.sOperatorA = "na";
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = true;
							jDisplay.html("ERROR: DIV 0");
							return false;
						}
						nResult = nValue1 / nValue2;
						if(oCurObj.data.bPercent==true)
						{
							nResult = nResult*100;
						}
						break;
					}
					case "power":
					{
						nResult = Math.pow(nValue1,nValue2);
						break;
					}
				}

				if(nResult>-1 && nResult<1)
				{
					nResult = nResult.toString();
					if(nResult.length>=20)
					{
						if(nResult.indexOf("e")!=-1)
						{
							var aParts = vResult.split("e");
							if(parseInt(aParts[1])<-20)
							{
								nResult = "0";
							}
							else
							{
								if(aParts[0].length>14)
								{
									aParts[0] = aParts[0].substr(0,13);
									if(parseFloat(aParts[0])==0)
									{
										nResult = "0";
									}
									else
									{
										nResult = aParts.join("e");
									}
								}
								else
								{
									nResult = aParts.join("e");
								}
							}
						}
						else
						{
							nResult = nResult.substr(0,19);
						}
					}
				}
				else
				{
					nResult = nResult.toString();
					if(nResult.length>=18)
					{
						if(nResult.indexOf(".")==-1)
						{
							oCurObj.data.sOperandA = "na";
							oCurObj.data.sOperatorA = "na";
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = true;
							jDisplay.html("ERROR: OVERFLOW");
							return false;
						}
						else
						{
							if(nResult.indexOf("e")!=-1)
							{
								var aParts = nResult.split("e");
								if(aParts[0].length>14)
								{
									aParts[0] = aParts[0].substr(0,13);
									nResult = aParts.join("e")
								}
							}
							else
							{
								nResult = nResult.substr(0,17);
							}
						}
					}
				}
				jDisplay.html(nResult);
				oCurObj.data.sOperandA = nResult;
				if(oCurObj.data.bEqual==false && oCurObj.data.bPercent==false)
				{
					oCurObj.data.sOperatorA = argobj.sKey;
				}
				oCurObj.data.bClean = true;
				return true;
			}

function Filter(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="glossary") return false;
				if(argobj.elem==null) return false;
				if($(argobj.elem).hasClass("cl-letter-selected")) return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jTerms = jContainer.find(".cl-term-item");
				var sLetter = $.trim($(argobj.elem).text()); 
				if(sLetter=="*")
				{
					jTerms.show();
				}
				else
				{
					jTerms.hide().each(function ()
					{
						var sText = $.trim($(this).html());
						if(sText.toUpperCase().charAt(0)==sLetter)
						{
							$(this).show();
						}
					});
				}
				jTerms.filter(":visible").filter(":first").click();
				jContainer.find(".cl-letter-item").addClass("cl-letter-idle").removeClass("cl-letter-selected");
				$(argobj.elem).removeClass("cl-letter-idle").addClass("cl-letter-selected");
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="glossary") return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jTerms = jContainer.find(".cl-term-item");
				if(argobj.ecode!=null || argobj.eitem!=null)
				{
					var sCode = $.trim(argobj.ecode);
					var sText = $.trim(argobj.eitem);
					if(sCode=="" && sText=="") return false;
					var jItem;
					if(sCode!="")
					{
						jItem = jTerms.filter("[data-code='" + sCode + "']");
						if(jItem.length!=0)
						{
							if(CLO[argobj.pid].data.sPosition=="closed" && CLO[argobj.pid].data.sState=="enabled")
							{
								CLO[argobj.pid].CallMethod({ sMethod: "Toggle" });
							}
							if(!jItem.hasClass("cl-term-selected"))
							{
								jItem.click();
							}
						}
					}
					else if(sText!="")
					{
						var sItemText;
						for(var i=0; i<jTerms.length; i++)
						{
							sItemText = $.trim($(jTerms[i]).text());
							if(sText==sItemText)
							{
								jItem = $(jTerms[i]);
								break;
							}
						}
						if(jItem.length!=0)
						{
							if(CLO[argobj.pid].data.sPosition=="closed" && CLO[argobj.pid].data.sState=="enabled")
							{
								CLO[argobj.pid].CallMethod({ sMethod: "Toggle" });
							}
							if(!jItem.hasClass("cl-term-selected"))
							{
								jItem.click();
							}
						}
					}
				}
				else
				{
					if(argobj.elem==null) return false;
					if($(argobj.elem).hasClass("cl-letter-selected")) return false;
					var sTermId = $(argobj.elem).attr("data-number");
					jTerms.removeClass("cl-term-selected").addClass("cl-term-idle");
					jContainer.find(".cl-term-desc-item").hide();
					jContainer.find(".cl-term-desc-item[data-number='" + sTermId + "']").show("fade");
					jContainer.find(".cl-term-item[data-number='" + sTermId + "']").removeClass("cl-term-idle").addClass("cl-term-selected");
				}
				return true;
			}

function Update(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jContainer = $(oCurObj.div).find(".cl-container");
				switch(oCurObj.data.sRole)
				{
					case "contents":
					{
						var jLIs = jContainer.find("ul.slide-list > li.slide-item");
						var sSlideId;
						var jLI;
						var bPrevVisited = true;
						for(var i=0; i<jLIs.length; i++)
						{
							jLI = $(jLIs[i]);
							sSlideId = jLI.attr("data-slide-id");
							if(sSlideId==CLZ.sCurrentSlideId)
							{
								jLI.removeClass("allowed forbidden visited unvisited").addClass("current");
								bPrevVisited = true;
							}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: sSlideId }))
							{
								jLI.removeClass("current unvisited forbidden").addClass("allowed visited");
								bPrevVisited = true;
							}
							else
							{
								if(bPrevVisited)
								{
									if(CL.oPath._TargetAllowed({ sSlideId: sSlideId }))
									{
										jLI.removeClass("current unvisited forbidden").addClass("allowed unvisited");
									}
									else
									{
										jLI.removeClass("allowed current").addClass("forbidden unvisited");
									}
									bPrevVisited = false;
								}
								else
								{
									if(!CLZ.bStrictOrder)
									{
										if(CL.oPath._TargetAllowed({ sSlideId: sSlideId }))
										{
											jLI.removeClass("current unvisited forbidden").addClass("allowed unvisited");
										}
										else
										{
											jLI.removeClass("allowed current").addClass("forbidden unvisited");
										}
									}
									else
									{
										jLI.removeClass("allowed current").addClass("forbidden unvisited");
									}
								}
							}
						}
						break;
					}
					case "settings":
					{
						var jTR;
						for(var sObjId in CLJ)
						{
							if(oCurObj.data.sObjToDisplay=="main" && CLJ[sObjId].bModule.toString()!="1") continue;
							if(oCurObj.data.sObjToDisplay=="other" && CLJ[sObjId].bModule.toString()=="1") continue;
							jTR = jContainer.find("tr[data-obj-id='" + sObjId + "']");
							if(oCurObj.data.sScoreDisplayType=="raw")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
							}
							else if(oCurObj.data.sScoreDisplayType=="rawmax")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore + "(" + CLJ[sObjId].nMax + ")");
							}
							else if(oCurObj.data.sScoreDisplayType=="scaled")
							{
								if(CLJ[sObjId].nMax!=0)
								{
									jTR.children("td.cl-sett-score").html( Math.round(100*CLJ[sObjId].nRawScore/CLJ[sObjId].nMax)/100 + "%");
								}
								else
								{
									jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
								}
							}
							if(oCurObj.data.bCustomIcons)
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS) });
								jTD.find("img").hide();
								jTD.find("img.sett-cs-" + CLJ[sObjId].sCS).show();
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS) });
								jTD.find("img").hide();
								jTD.find("img.sett-ss-" + CLJ[sObjId].sSS).show();
							}
							else
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS) });
								jTD.find("div.cl-sett-status").removeClass("cl-sett-status-n cl-sett-status-i cl-sett-status-c").addClass("cl-sett-status-" + CLJ[sObjId].sCS);
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS) });
								jTD.find("div.cl-sett-status").removeClass("cl-sett-status-u cl-sett-status-p cl-sett-status-f").addClass("cl-sett-status-" + CLJ[sObjId].sSS);
							}
						}
						break;
					}
					case "comment":
					{
						var jxComments = CLS[CLZ.sCurrentSlideId].jxNode.children("comments");
						if(jxComments.length!=0)
						{
							if(jxComments.text()!="")
							{
								var sText = $("<div/>").html(jxComments.text()).text();
								if(oCurObj.data.sIfNoComment=="disable")
								{
									oCurObj.data.bNaviLockedByAction = false;
									oCurObj.CallMethod({ sMethod: "Enable" });
								}
								else if(oCurObj.data.sIfNoComment=="hide")
								{
									oCurObj.Show();
								}
								jContainer.find(".cl-comm-none").hide();
								jContainer.find(".cl-comm-body").html(sText).show();
							}
							else
							{
								if(oCurObj.data.sIfNoComment=="text")
								{
									jContainer.find(".cl-comm-none").show();
									jContainer.find(".cl-comm-body").hide();
								}
								else if(oCurObj.data.sIfNoComment=="disable")
								{
									oCurObj.data.bNaviLockedByAction = true;
									oCurObj.CallMethod({ sMethod: "Disable" });
								}
								else
								{
									oCurObj.Hide();
								}
							}
						}
						else
						{
							if(oCurObj.data.sIfNoComment=="text")
							{
								jContainer.find(".cl-comm-none").show();
								jContainer.find(".cl-comm-body").hide();
							}
							else if(oCurObj.data.sIfNoComment=="disable")
							{
								oCurObj.data.bNaviLockedByAction = true;
								oCurObj.CallMethod({ sMethod: "Disable" });
							}
							else
							{
								oCurObj.Hide();
							}
						}
						break;
					}
				}
				return true;
			}

function SearchTip(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(!oCurObj.data.bSearchTip)
				{
					return false;
				}
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jTips = jContainer.find(".cl-search-tips");
				if(!jTips.is(":visible"))
				{
					jContainer.find(".cl-search-results-list, .cl-search-found, .cl-search-no-results").hide();
					jTips.fadeIn("fast");
				}
				return true;
			}

function Search(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jContainer = $(oCurObj.div).find(".cl-container");
				var sStr = jContainer.find("input.cl-search-field").val();
				sStr = $.trim(sStr); 
				if(sStr=="") return false;
				var sCase = oCurObj.jxParams.children("search_case").text();
				var bCase = false;
				if(sCase=="no_user" || sCase=="yes_user")
				{
					bCase = (jContainer.find("select.cl-search-case").val()=="yes");
				}
				else
				{
					bCase = (sCase=="yes");
				}
				var sLogic = oCurObj.jxParams.children("search_logic").text();
				if(sLogic=="OR_user" || sLogic=="AND_user")
				{
					sLogic = (jContainer.find("select.cl-search-logic").val()=="yes") ? "AND" : "OR";
				}
				var sScope = oCurObj.jxParams.children("search_scope").text();
				var aResults = CL.Search({ sString: sStr, bCase: bCase, sLogic: sLogic, sScope: sScope });
				if(oCurObj.data.bSearchTip)
				{
					jContainer.find(".cl-search-tips").hide();
				}
				if(aResults.length==0)
				{
					jContainer.find(".cl-search-results-list, .cl-search-found").hide();
					jContainer.find(".cl-search-no-results").show();
				}
				else
				{
					var jUL = jContainer.find(".cl-search-results-list");
					jUL.html("").show();
					jContainer.find(".cl-search-found").show();
					jContainer.find(".cl-search-found").find(".cl-search-found-qty").html(aResults.length);
					jContainer.find(".cl-search-no-results").hide();
					var jLI;
					var jTemplate = jContainer.find("li.cl-search-item-template");
					var sReturnFormat = oCurObj.jxParams.children("search_return_format").text();
					var sSlideId;
					for(var i=0; i<aResults.length; i++)
					{
						sSlideId = $(aResults[i].slide).attr("id");
						jLI = jTemplate.clone(true).removeClass("cl-search-item-template").addClass("cl-search-results-item").attr({ "data-slide-id": sSlideId }).appendTo(jUL);
						jLI.find(".item-name").html($(aResults[i].slide).attr("name"));
						if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: sSlideId }) || sSlideId==CLZ.sCurrentSlideId)
						{
							jLI.addClass("allowed visited").on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { elem: this } }) });
						}
						if(sReturnFormat=="extended")
						{
							var aStrings = aResults[i].strings;
							var sString = "";
							for(var j=0; j<aStrings.length; j++)
							{
								if(j==0)
								{
									sString += aStrings[j];
								}
								else
								{
									sString += "<br>" + aStrings[j];
								}
							}
							jLI.find(".item-text").html(sString);
						}
						else
						{
							jLI.find(".item-text").hide();
						}
					}
				}
				return true;
			}

function Navigate(argobj)
			{
				if(argobj.elem==null) return false;
				if($(argobj.elem).hasClass("allowed"))
				{
					var oCurObj = CLO[argobj.pid];
					var sTargetId = $(argobj.elem).attr("data-slide-id");
					CL.Navigation.GoTo({ sTargetType: "slide", sTargetId: sTargetId });
					CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
					if(oCurObj.data.sSoundClick!="none")
					{
						CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
					}
				}
				return true;
			}

function Open(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sPosition=="closed" && oCurObj.data.sState=="enabled")
				{
					oCurObj.CallMethod({ sMethod: "Toggle" });
				}
				return true;
			}

function Close(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sPosition=="open" && oCurObj.data.sState=="enabled")
				{
					oCurObj.CallMethod({ sMethod: "Toggle" });
				}
				return true;
			}

function Disable(argobj)
			{
				if(CLO[argobj.pid].data.sPosition=="open")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Toggle" });
				}
				CLO[argobj.pid].data.sState=="disabled";
				return true;
			}

function Enable(argobj)
			{
				CLO[argobj.pid].data.sState=="enabled";
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "disabled":
					{
						return (CLO[argobj.pid].data.sState=="disabled") ? "1" : "0";
					}
					case "open":
					{
						return (CLO[argobj.pid].data.sPosition=="open") ? "1" : "0";
					}
				}
				return "";
			}

function Toggle(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurDiv = $(oCurObj.div);
				if(oCurObj.data.sPosition=="closed")
				{
					if(oCurObj.data.bModal)
					{
						CL.Modal({ bOn: true, sSrcId: argobj.pid });
					}
					oCurObj.data.sPosition = "transition_open";
					jCurDiv.css({ "z-index": 5001 });
					if(oCurObj.data.bTranIn)
					{
						oCurObj.Show({ bActionCall: true, bUseTransition: true, iTransition: oCurObj.data.iTranIn, iDuration: oCurObj.data.iDurIn, fnCallback: function () { CLO[argobj.pid].data.sPosition = "open"; } });
					}
					else
					{
						oCurObj.Show({ bActionCall: true, bUseTransition: false });
						oCurObj.data.sPosition = "open";
					}
					if(oCurObj.data.aTogglers.length>0)
					{
						for(var i=0; i<oCurObj.data.aTogglers.length; i++)
						{
							if(CLO[oCurObj.data.aTogglers[i]].oMethods["Off"]!=null)
							{
								CLO[oCurObj.data.aTogglers[i]].CallMethod({ sMethod: "Off" });
							}
						}
					}
				}
				else
				{
					if(oCurObj.data.bModal)
					{
						CL.Modal({ bOn: false, sSrcId: argobj.pid });
					}
					oCurObj.data.sPosition = "transition_close";
					if(oCurObj.data.bTranOut)
					{
						oCurObj.Hide({ bActionCall: true, bUseTransition: true, iTransition: oCurObj.data.iTranOut, iDuration: oCurObj.data.iDurOut, fnCallback: function () { CLO[argobj.pid].data.sPosition = "closed"; } });
					}
					else
					{
						oCurObj.Hide({ bActionCall: true, bUseTransition: false });
						oCurObj.data.sPosition = "closed";
					}
					if(oCurObj.data.aTogglers.length>0)
					{
						for(var i=0; i<oCurObj.data.aTogglers.length; i++)
						{
							if(CLO[oCurObj.data.aTogglers[i]].oMethods["On"]!=null)
							{
								CLO[oCurObj.data.aTogglers[i]].CallMethod({ sMethod: "On" });
							}
						}
					}
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function Constructor(argobj)
		{
			var oCurObj = CLO[argobj.pid]; 
			oCurObj.data = { bModal: (oCurObj.jxParams.find("modal").text()=="yes") };
			$(oCurObj.div).find(".cl-btn, .cl-close-on-img").on("click", function () { oCurObj.CallMethod({ sMethod: "HandleClick", oMethodArgs: { elem: this } }); });
			return true;
		}

function Adjust(argobj)
		{
			var oCurObj = CLO[argobj.pid];
			var jBox = $("#" + argobj.pid + "_ZOOM"); 
			var jImg = jBox.find("img");
			var iIW = jImg.width();
			var iIH = jImg.height();
			var nIRatio = iIW/iIH;
			var iBW = $(CL.oBoard).innerWidth();
			var iBH = $(CL.oBoard).innerHeight();
			var nBRatio = iBW/iBH;
			if(nIRatio==nBRatio)
			{
				jImg.css({ "width": (iBW-30) + "px", "height": (iBH-30) + "px" });
				jBox.css({ "width": (iBW-10) + "px", "height": (iBH-10) + "px", "left": "5px", "top": "5px" }).hide();
			}
			else if(nIRatio>nBRatio) // hor img
			{
				var nRatio = iIW/iBW;
				var iW = (iBW-30);
				var iH = (Math.floor(iIH/nRatio)-30);
				var iMargin = 0.5*(iBH-iH-30);
				jImg.css({ "width": iW + "px", "height": iH + "px", "margin-top": iMargin + "px" });
				jBox.css({ "width": (iBW-10) + "px", "height": (iBH-10) + "px", "left": "5px", "top": "5px" }).hide();
			}
			else
			{
				var nRatio = iIH/iBH;
				var iW = (Math.floor(iIW/nRatio)-30);
				var iH = (iBH-30);
				var iMargin = 0.5*(iBW-iW-30);
				jImg.css({ "width": iW + "px", "height": iH + "px", "margin-left": iMargin + "px" });
				jBox.css({ "width": (iBW-10) + "px", "height": (iBH-10) + "px", "left": "5px", "top": "5px" }).hide();
			}
			oCurObj.data.bAdjusted = true;
			return true;
		}

function HandleClick(argobj)
		{
			var oCurObj = CLO[argobj.pid];
			if(!oCurObj.data.bAdjusted) return false;
			var jElem = $(argobj.elem);
			if(jElem.hasClass("cl-btn-magnify"))
			{
				oCurObj.CallMethod({ sMethod: "Open" });
			}
			else
			{
				oCurObj.CallMethod({ sMethod: "Close" });
			}
			return true;
		}

function Open(argobj)
		{
			var oCurObj = CLO[argobj.pid];
			var jBox = $("#" + argobj.pid + "_ZOOM");
			if(oCurObj.data.bModal)
			{
				CL.Modal({ bOn: true, sSrcId: argobj.pid });
			}
			jBox.css({ "z-index": 5001 }).appendTo(CL.oBoard).fadeIn();
			return true;
		}

function Close(argobj)
		{
			var oCurObj = CLO[argobj.pid];
			var jBox = $("#" + argobj.pid + "_ZOOM");
			if(oCurObj.data.bModal)
			{
				CL.Modal({ bOn: false, sSrcId: argobj.pid });
			}
			jBox.css({ "z-index": 5001 }).fadeOut("fast", function () { $(this).appendTo($(oCurObj.div).find(".cl-container")); });
			return true;
		}
